SELECT t1.TaskId AS Id, t1.DefinitionId AS ProcessDefinitionId, t1.DefinitionName AS ProcessDefinitionName, t1.DefinitionKey AS ProcessDefinitionKey, t1.InstanceId AS ProcessInstanceId, t1.TaskName AS NAME, t1.TaskDefinitionKey, t1.BusinessKey AS ProcessInstanceBusinessKey, t1.Description, t1.CreateTime FROM ( SELECT res.ID_ AS TaskId, res.PROC_DEF_ID_ AS DefinitionId, arp.NAME_ AS DefinitionName, arp.KEY_ AS DefinitionKey, res.PROC_INST_ID_ AS InstanceId, res.NAME_ AS TaskName, res.TASK_DEF_KEY_ AS TaskDefinitionKey, res.CATEGORY_ AS BusinessKey, res.DESCRIPTION_ AS Description, res.CREATE_TIME_ AS CreateTime FROM ACT_RU_TASK RES INNER JOIN act_re_procdef arp ON RES.PROC_DEF_ID_ = arp.ID_ INNER JOIN ACT_HI_PROCINST HPI ON RES.PROC_INST_ID_ = HPI.ID_ INNER JOIN act_hi_taskinst aht ON RES.ID_ = aht.ID_
WHERE (aht.END_TIME_ IS NOT NULL OR HPI.END_TIME_ IS NULL ) AND RES.ASSIGNEE_ IS NULL OR RES.ASSIGNEE_ = #{srf.sessioncontext.srfuserid} OR ( RES.ASSIGNEE_ IS NULL AND EXISTS ( SELECT LINK.ID_ FROM ACT_RU_IDENTITYLINK LINK WHERE LINK.TASK_ID_ = RES.ID_ AND LINK.USER_ID_ = #{srf.sessioncontext.srfuserid} ) ) ) t1
